import React, { useState } from 'react';

function AppTwo() {
    const { count, setCount } = useState(0);
    function handleButtonClick() {
        setCount(count++);
    }

    return (
        <div>
            <h1>{count}</h1>
            <button class="container button" onClick={handleButtonClick}>Increment</button>
            <button class="container button" onClick={handleButtonClick}>Decrement</button>
            <button class="container button" onClick={handleButtonClick}>Reset</button>
        </div>
    );
}

export default AppTwo;
